#!/bin/bash

while read line; do arr=($line) &&   python3 ./munge.py --ss /mnt/disks/r7/prs/aki-phenos/${arr[0]} -o /mnt/disks/r7/prs/aki-phenos/munge/ --effect_type ${arr[8]} --variant ${arr[9]} --chrom ${arr[10]} --pos ${arr[11]} --ref ${arr[12]} --alt ${arr[13]} --effect ${arr[14]} --pval ${arr[15]}  --rsid-map /mnt/disks/r7/data/variant_mapping/finngen.rsid.map.tsv --chrompos-map /mnt/disks/r7/data/variant_mapping/finngen.variants.tsv --chainfile /mnt/disks/r7/data/lift/empty_file.txt  |& tee -a /mnt/disks/r7/prs/aki-phenos/munge.log; done < <(cat /mnt/disks/r7/prs/aki-phenos/PRS_aki.txt| sed -E 1d  )
